import React  from 'react'
import {AppBar, Toolbar, IconButton, Badge, MenuItem, Menu, Typography} from '@material-ui/core';
import {ShoppingCart } from '@material-ui/icons'
import { Link, useLocation } from 'react-router-dom'
import assets from '../../assets/assets';

import useStyles from './styles.js';
import Logo from '../../assets/logo2.png'

import { connect } from "react-redux";

 
const Navbar = ({ cart }) => {
    const classes = useStyles()
    const location = useLocation();
    const [cartCount, setCartCount] = useState(0);

    useEffect(() => {
        let count = 0;
        cart.forEach((item) => {
          count += item.qty;
        });
    
        setCartCount(count);
      }, [cart, cartCount]);

    if(location.pathname)
    return (
        <>

            <AppBar position='fixed' className={classes.appBar} color='inherit'>
                <Toolbar>
                    <Typography component={Link} to='/' variant='h6' className={classes.title} color='inherit'>
                        <img src={Logo} alt='Tea' height='25px' className={classes.image}/>
                        Tea Shop
                    </Typography>

                    <Typography component={Link} to='/login' variant='caption' className={classes.log} color='inherit'>
                        Log In  
                    </Typography>

                    <Typography variant='caption' className={classes.sign} color='inherit'>
                        Sign up 
                    </Typography>




                    <div className={classes.grow}/>
                    {location.pathname == '/' && (
                    <div className={classes.button}>
                        <IconButton component={Link} to='/cart' arialabel='show cart items' color='inherit'>
                            <Badge badgeContent={{cartCount}} color='secondary'>
                                <ShoppingCart/>
                            </Badge>
                        </IconButton>


                    </div>)}
                </Toolbar>


            </AppBar>
            
        </>
    )
}

const mapStateToProps = (state) => {
    return {
      cart: state.shop.cart,
    };
  };
  
  export default connect(mapStateToProps)(Navbar);
