const Cart = ({ items, onAdd }) => {


    const classes = useStyles();


    

    return (

        <main className={classes.content}>
        
        <Typography variant="h2" component="h2" className={classes.title}>Your shopping cart</Typography>

        <Box sx={{ flexDirection: 'row' , justifyContent: 'space-evenly' , alignItems: 'center', }} className={classes.bar}>
            <div className={classes.prod} >
                Product:
            </div>

            <div className={classes.more}>
                <span className={classes.col}>+</span>
                
                <button className={classes.col}>-</button>
            </div>
        </Box>

        <div>
        {items.length === 0 && <div>No Items</div>}
        </div>
        <Grid >
        
            {items.map((items) => (
                <Grid item key={items.id} > 
                <CartItem item={items} />
                </Grid>
            ))}
        </Grid>

        <div>
                    <h1>ORDER SUMMARY</h1>
                    <div>
                      <span>Subtotal</span>
                      <span>$ 80</span>
                    </div>

                    <div>
                      <h1>Estimated Shipping</h1>
                      <span>$ 5.90</span>
                    </div>
                    <div>
                      <h1>Shipping Discount</h1>
                      <span>$ -5.90</span>
                    </div>

                    <div type="total">
                      <h1>Total</h1>
                      <span>$ 80</span>
                    </div>
                    <button>CHECKOUT NOW</button>
                  </div>
        </main>


    )};

export default Cart;



